package operatorinjava;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a=76, b=75;
		
		System.out.println("value of a :"+a+" value of b :"+b);
		
		boolean c = a < b;
		System.out.println("a is less than b :"+c);
		
		c = a > b;
		System.out.println("a is greater than b :"+c);
		
		c = a <= b;
		System.out.println("a is less than equal to b :"+c);
		
		c = a <= b;
		System.out.println("a is greater than equal to b :"+c);
		
		c = a==b;
		System.out.println("a is equal to b :"+c);
		
		c = a != b;
		System.out.println("a is not equal to b :"+c);

	}

}
